import React, { createElement, useState } from 'react'
import { storiesOf } from '@storybook/react'
import SuccessModal from './SuccessModal'
import { Dialog } from '@material-ui/core'
import notes from './notes.md'

const stories = storiesOf('Success Modal', module)

stories.add(
  'default',
  () =>
    createElement(() => {
      const [open, setOpen] = useState(true)

      const handleClose = () => {
        setOpen(false)
      }
      const handleBtn = () => {
        console.log('Testing')
      }

      return (
        <Dialog open={open}>
          <SuccessModal
            text={'You have removed “HOPE Closers” from PP23'}
            btnText={'ADD CAMPAIGN'}
            closeFn={handleClose}
            btnFn={handleBtn}
          />
        </Dialog>
      )
    }),
  { notes: { markdown: notes } }
)

stories.add(
  'without add button',
  () =>
    createElement(() => {
      const [open, setOpen] = useState(true)

      const handleClose = () => {
        setOpen(false)
      }

      return (
        <Dialog open={open}>
          <SuccessModal
            text={'You have removed “HOPE Closers” from PP23'}
            closeFn={handleClose}
          />
        </Dialog>
      )
    }),
  { notes: { markdown: notes } }
)

stories.add(
  'warning',
  () =>
    createElement(() => {
      const [open, setOpen] = useState(true)

      const handleClose = () => {
        setOpen(false)
      }
      const handleBtn = () => {
        console.log('Testing')
      }

      return (
        <Dialog open={open}>
          <SuccessModal
            text={'Are you sure you want to remove “HOPE Closers” from PP23?'}
            btnText={'YES, REMOVE'}
            closeFn={handleClose}
            btnFn={handleBtn}
            warning
          />
        </Dialog>
      )
    }),
  { notes: { markdown: notes } }
)

stories.add(
  'new user modal',
  () =>
    createElement(() => {
      const [open, setOpen] = useState(true)

      const handleClose = () => {
        setOpen(false)
      }
      const handleBtn = () => {
        console.log('Testing')
      }

      return (
        <Dialog open={open}>
          <SuccessModal
            user
            text={'You have created Cherry Zuniga'}
            closeFn={handleClose}
            btnFn={handleBtn}
          />
        </Dialog>
      )
    }),
  { notes: { markdown: notes } }
)
