import React, { useState, useEffect } from "react";
import { Search as SearchIcon, Settings as GearIcon } from "@material-ui/icons";
import { Link } from "react-router-dom";
import {
  Paper,
  Grid,
  Collapse,
  List,
  ListItem,
  ListItemText,
  InputAdornment,
  TextField,
  TableRow,
  Table,
  TableBody,
  Menu,
  MuiThemeProvider
} from "@material-ui/core";
import { ActiveCell, LiveCell } from "../table-cells";
import { theme, useStyles } from "./styles";

interface Props {
  title: string;
  userData: any[];
  headers: string[];
  pathnameData?: any;
  link?: string;
  settings?: any;
  setActiveDataMethod?: any;
  loading?: boolean;
  active?: any;
  live?: any;
  modalFunc?: any;
  classicSearch?: any;
  customTitle?: any;
  typed?: any;
}

const SearchBar: React.FC<Props> = ({
  title,
  userData,
  headers,
  pathnameData,
  link,
  settings,
  setActiveDataMethod,
  loading,
  active,
  live,
  modalFunc,
  classicSearch,
  customTitle,
  typed
}) => {
  const classes = useStyles({});
  const [results, setResults] = useState<object[][] | null | any>(null);
  const [DummyResults, setDummyResults] = useState<any[]>(userData);
  const [anchorEl, setAnchorEl] = React.useState<Element | null>(null);
  const [textSearch, setTextSearch] = React.useState<string>("");

  const handleClick = (event: any, data: any) => {
    setAnchorEl(event.currentTarget);
    setActiveDataMethod(data);
  };

  const handClose = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    if (loading) {
      setTextSearch("");
      setResults(null);
      setAnchorEl(null);
    }
  }, [setTextSearch, loading, setResults, setAnchorEl]);

  let filtered: any[] = [];

  const handleSearch = (e: any) => {
    setTextSearch(e.target.value);
    const regex = /[*()?+[\\]/gi;
    if (typed) {
      typed(e.target.value);
    }
    let event = e.target.value.replace(regex, "");
    if (event.length !== 0) {
      const arr = JSON.stringify(userData, function(key, value) {
        return value || false;
      });
      const campaigns = JSON.parse(arr);
      let filteredData: any[] = [];

      campaigns.map((data: any) => {
        headers.filter(head => {
          if (typeof data[head] !== "boolean") {
            if (data[head].toLowerCase().includes(event.toLowerCase())) {
              filteredData.push(data);
            }
          }
        });
      });

      filtered.push([...filteredData]);

      if (classicSearch) {
        classicSearch(filtered[0]);
      } else {
        setDummyResults(filtered[0]);
        setResults(filtered[0]);
      }
    } else {
      if (classicSearch) {
        classicSearch(filtered[0]);
      } else {
        filtered = [];
        setResults(null);
      }
    }
  };

  const handlePathname = (result: any) => {
    return `${pathnameData.firstLink}${
      pathnameData.fetchData.length > 1
        ? pathnameData.fetchData[0] === "uuid"
          ? pathnameData.fetchData[1] === "slug"
            ? result.uuid + "/" + result.slug
            : null
          : pathnameData.fetchData[0] === "slug"
          ? pathnameData.fetchData[1] === "uuid"
            ? result.slug + "/" + result.uuid
            : null
          : null
        : pathnameData.fetchData[0] === "uuid"
        ? result.uuid
        : pathnameData.fetchData[0] === "slug"
        ? result.slug
        : null
    }${pathnameData.lastLink}`;
  };

  return (
    <MuiThemeProvider theme={theme}>
      <div className={classes.root}>
        <Grid container style={{ width: "100%" }}>
          <Grid item xs={12} lg={12}>
            <TextField
              disabled={loading !== undefined ? loading : false}
              onChange={e => handleSearch(e)}
              data-cy="search-bar"
              id="standard-full-width"
              value={textSearch}
              placeholder={customTitle ? customTitle : `Search for ${title}`}
              fullWidth
              margin="normal"
              autoComplete="off"
              style={{
                fontSize: 50,
                padding: "5px 15px 15px 15px",
                width: "100%"
              }}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <SearchIcon style={{ color: "#bbb" }} fontSize="small" />
                  </InputAdornment>
                ),
                classes: {
                  underline: classes.inputField,
                  root: classes.inputField
                }
              }}
            />
          </Grid>
          {results !== null ? (
            results.length !== 0 ? null : (
              <Grid
                item
                xs={12}
                lg={12}
                style={{ paddingLeft: 10, paddingRight: 10, marginTop: -15 }}
              ></Grid>
            )
          ) : null}
          <Collapse
            in={
              results !== null ? (results.length !== 0 ? false : true) : false
            }
            timeout={1000}
            unmountOnExit
            style={{ width: "100%" }}
          >
            {" "}
            <Paper
              style={{
                width: "100%",
                fontSize: "18px",
                color: "#7c8a97",
                padding: 20,
                backgroundColor: "white",
                zIndex: 1
              }}
            >
              No results - Try searching for{" "}
              {headers === undefined ? "other fields" : `${headers}`}
            </Paper>
          </Collapse>

          <Collapse
            in={
              results !== null ? (results.length === 0 ? false : true) : false
            }
            timeout={1000}
            unmountOnExit
            style={{ width: "100%" }}
          >
            <List
              component="nav"
              aria-label="search results"
              style={{
                maxHeight: 300,
                height: 300,
                overflow: "auto",
                width: "100%"
              }}
            >
              {results === null || results.length === 0
                ? DummyResults.map((result: any, i: number) => {
                    return (
                      <ListItem key={i}>
                        <Grid container>
                          <Grid item>
                            <ListItemText
                              primary={
                                title === "User" || title === "Prospect"
                                  ? `${result.first_name} ${result.last_name}`
                                  : `${
                                      result.name === undefined
                                        ? result[Object.keys(result)[0]]
                                        : result.name
                                    }`
                              }
                            />
                          </Grid>
                          <Grid
                            item
                            style={{ marginLeft: "auto", paddingRight: 20 }}
                          >
                            <Grid container spacing={5}>
                              <Grid item>
                                {active !== undefined && active === true && (
                                  <Table>
                                    <TableBody>
                                      <TableRow>
                                        <ActiveCell>
                                          {title === "User"
                                            ? result.is_active
                                            : result.active}
                                        </ActiveCell>
                                      </TableRow>
                                    </TableBody>
                                  </Table>
                                )}
                              </Grid>
                              {settings !== undefined && (
                                <Grid item style={{ marginTop: 10 }}>
                                  <GearIcon style={{ color: "#777777" }} />
                                </Grid>
                              )}
                            </Grid>
                          </Grid>
                        </Grid>
                      </ListItem>
                    );
                  })
                : results.map((result: any, i: number) => {
                    return (
                      <div key={i}>
                        {link !== undefined ? (
                          <Link
                            key={result.uuid}
                            style={{
                              textDecoration: "none",
                              color: "#000"
                            }}
                            to={{
                              pathname: handlePathname(result),
                              state: {
                                company: result
                              }
                            }}
                          >
                            <ListItem
                              button
                              onClick={() => {
                                localStorage.setItem(
                                  `companyslug`,
                                  result.slug
                                );
                                localStorage.setItem(
                                  `campaignuuid`,
                                  result.uuid
                                );
                              }}
                            >
                              <Grid container>
                                <Grid item>
                                  <ListItemText
                                    primary={
                                      title === "User" || title === "Prospect"
                                        ? `${result.first_name} ${result.last_name}`
                                        : `${
                                            result.name === undefined
                                              ? result[Object.keys(result)[0]]
                                              : result.name
                                          }`
                                    }
                                  />
                                </Grid>
                                <Grid
                                  item
                                  style={{
                                    marginLeft: "auto",
                                    paddingRight: 20
                                  }}
                                >
                                  <Grid container spacing={5}>
                                    <Grid item>
                                      {live !== undefined && live === true && (
                                        <Table>
                                          <TableBody>
                                            <TableRow>
                                              <LiveCell>{result.live}</LiveCell>
                                            </TableRow>
                                          </TableBody>
                                        </Table>
                                      )}
                                    </Grid>
                                    <Grid item>
                                      {active !== undefined && active === true && (
                                        <Table>
                                          <TableBody>
                                            <TableRow>
                                              <ActiveCell>
                                                {title === "User"
                                                  ? result.is_active
                                                  : result.active}
                                              </ActiveCell>
                                            </TableRow>
                                          </TableBody>
                                        </Table>
                                      )}
                                    </Grid>
                                    {settings !== undefined && (
                                      <Grid item style={{ marginTop: 10 }}>
                                        <GearIcon
                                          style={{ color: "#777777" }}
                                          onClick={e => handleClick(e, result)}
                                        />
                                        <Menu
                                          onClose={handClose}
                                          style={{ marginTop: 40 }}
                                          id="simple-menu"
                                          anchorEl={anchorEl}
                                          open={Boolean(anchorEl)}
                                        >
                                          {settings}
                                        </Menu>
                                      </Grid>
                                    )}
                                  </Grid>
                                </Grid>
                              </Grid>
                            </ListItem>
                          </Link>
                        ) : (
                          <>
                            <ListItem
                              button
                              onClick={
                                modalFunc !== undefined
                                  ? () => modalFunc(result)
                                  : () => null
                              }
                              key={result.uuid}
                            >
                              <Grid container>
                                <Grid item>
                                  <ListItemText
                                    primary={
                                      title === "User" || title === "Prospect"
                                        ? `${result.first_name} ${result.last_name}`
                                        : `${
                                            result.name === undefined
                                              ? result[Object.keys(result)[0]]
                                              : result.name
                                          }`
                                    }
                                  />
                                </Grid>
                                <Grid
                                  item
                                  style={{
                                    marginLeft: "auto",
                                    paddingRight: 20
                                  }}
                                >
                                  <Grid container spacing={5}>
                                    <Grid item>
                                      {active !== undefined && active === true && (
                                        <Table>
                                          <TableBody>
                                            <TableRow>
                                              <ActiveCell>
                                                {title === "User"
                                                  ? result.is_active
                                                  : result.active}
                                              </ActiveCell>
                                            </TableRow>
                                          </TableBody>
                                        </Table>
                                      )}
                                    </Grid>
                                    {settings !== undefined && (
                                      <Grid item style={{ marginTop: 10 }}>
                                        <GearIcon
                                          style={{ color: "#777777" }}
                                          onClick={e => handleClick(e, result)}
                                        />
                                      </Grid>
                                    )}
                                  </Grid>
                                </Grid>
                              </Grid>
                            </ListItem>
                          </>
                        )}
                      </div>
                    );
                  })}
              <Menu
                onClose={handClose}
                style={{ marginTop: 40 }}
                id="simple-menu"
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
              >
                {settings}
              </Menu>
            </List>
          </Collapse>
        </Grid>
      </div>
    </MuiThemeProvider>
  );
};

SearchBar.defaultProps = {
  title: "Search Data",
  userData: [{ name: "sample" }, { name: "samples1" }],
  headers: ["name"],
  pathnameData: "",
  loading: false
};

export { SearchBar };
