import React from "react";
import Grid from "@material-ui/core/Grid";
import { useStyles } from "./style";


const TableNoResult = ({
  noHeader,
  mainMessage,
  subMessage,
  renderButton,
  icon,
  headerText
}) => {
  const classes = useStyles();
  return (
    <>
      <Grid container direction="column" className={classes.root}>
        {!noHeader ? (
          <Grid
            alignItems="center"
            className={classes.header}
            container
            justify="center"
          >
            <Grid
              item
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                flexDirection: "row"
              }}
            >
              {icon}
              <h1 style={{ marginLeft: "22px" }}>{headerText} </h1>
            </Grid>
          </Grid>
        ) : null}
        <Grid
          alignItems="center"
          className={classes.message}
          container
          direction="column"
          justify="center"
        >
          <Grid item>
            <span className={classes.mainMessage}>{mainMessage}</span>
          </Grid>

          <Grid item className={classes.subMessageContainer}>
            <span className={classes.subMessage}>{subMessage}</span>
          </Grid>
          <Grid item className={classes.buttonContainer}>
            {renderButton}
          </Grid>
        </Grid>
      </Grid>
    </>
  );
};
export { TableNoResult };
