import React, { Component } from 'react'

import { withStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import CircularProgress from '@material-ui/core/CircularProgress'

const styles = theme => ({
  root: {
    fontFamily: theme.typography.fontFamily,
    border: 'solid 1px #F1F1F1'
  },
  header: {
    color: theme.palette.primary.contrastText,
    height: 190,
    backgroundColor: '#7c8a97',
    '& h1': {
      fontSize: 48,
      fontWeight: theme.typography.fontWeightLight
    }
  },
  message: {
    marginTop: '15px',
    padding: '75px 0',
    fontColor: theme.palette.text.primary,
    '& p': {
      color: '#7c8a97',
      marginTop: 40
    },
    height: 560
  },
  icon: {
    color: theme.palette.primary.contrastText,
    width: 60,
    height: 60,
    padding: '0 23px'
  },
  mainMessage: {
    fontSize: 18,
    fontWeight: 550,
    color: '#7c8a97'
  },
  subMessage: {
    fontSize: 16,
    color: '#777',
    fontWeight: 540
  },
  subMessageContainer: {
    paddingTop: 15,
    width: 350,
    textAlign: 'center'
  },
  buttonContainer: {
    paddingTop: 25
  }
})

class TableNoResult extends Component {
  constructor(props) {
    super(props)
    this.state = {
      loading: this.props.loading
    }
  }

  render() {
    const { classes, icon } = this.props
    return (
      <>
        <Grid container direction="column" className={classes.root}>
          {!this.props.noHeader ? (
            <Grid
              alignItems="center"
              className={classes.header}
              container
              justify="center"
            >
              <Grid
                item
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  flexDirection: 'row'
                }}
              >
                {icon}
                <h1 style={{ marginLeft: '22px' }}>{this.props.headerText} </h1>
              </Grid>
            </Grid>
          ) : null}
          <Grid
            alignItems="center"
            className={classes.message}
            container
            direction="column"
            justify="center"
          >
            <Grid item>
              <span className={classes.mainMessage}>
                {this.props.mainMessage}
              </span>
            </Grid>

            <Grid item className={classes.subMessageContainer}>
              <span className={classes.subMessage}>
                {this.props.subMessage}
              </span>
            </Grid>
            <Grid item className={classes.buttonContainer}>
              {this.props.renderButton}
            </Grid>
          </Grid>
        </Grid>
      </>
    )
  }
}
export default withStyles(styles)(TableNoResult)
