import React, { ReactPortal, SyntheticEvent } from "react";
import PropTypes from "prop-types";
import {
    Dialog,
    DialogTitle,
    DialogContent,
} from "@material-ui/core";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";


interface Props{
    open: boolean
    width?: number
    height?: number
    onClose: Function
    title: string
    contentStyle?: object
    children: React.ReactNode
}

const Modal: React.FC<Props> = ({ open, width, height, onClose, title, contentStyle, children }) => {
    return (
        <div>
            <Dialog
                maxWidth="xl"
                open={open}
                PaperProps={{
                    square: true,
                    style: {
                        width: width ? width : 420,
                        height: height ? height : "auto"
                    }
                }}
                onClose={() => {
                    onClose();
                }}
            >
                <DialogTitle style={{
                    backgroundColor: "#5f7d98",
                    color: "#FFF",
                    padding: 10
                }}>
                    <div
                        style={{
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center"
                        }}
                    >
                        <div style={{ width: 30 }}>&nbsp;</div>
                        <div>
                            <span style={{
                                fontSize: 20,
                                fontWeight: 550
                            }} >
                                {title}
                            </span>
                        </div>
                        <div>
                            <IconButton
                                onClick={() => {
                                    onClose();
                                }}
                            >
                                <CloseIcon style={{ color: "#FFF" }} />
                            </IconButton>
                        </div>
                    </div>
                </DialogTitle>
                <DialogContent dividers style={{ ...contentStyle }}>
                    {children}
                </DialogContent>
            </Dialog>
        </div>
    );
}

Modal.defaultProps = {
    title: 'Test Default!',
    open: false
} as Partial<Props>;

export default (Modal);
