import React, { useState } from 'react'
import clsx from 'clsx'
import { makeStyles } from '@material-ui/core/styles'
import CssBaseline from '@material-ui/core/CssBaseline'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
import IconButton from '@material-ui/core/IconButton'
import MenuIcon from '@material-ui/icons/Menu'
import AccountCircleIcon from '@material-ui/icons/AccountCircle'
import SideNav from '../side-nav/SideNav'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore'
import Grow from '@material-ui/core/Grow'
import Paper from '@material-ui/core/Paper'
import Popper from '@material-ui/core/Popper'
import MenuItem from '@material-ui/core/MenuItem'
import MenuList from '@material-ui/core/MenuList'
import ListItemIcon from '@material-ui/core/ListItemIcon'
import ListItemText from '@material-ui/core/ListItemText'
import PowerSettingsNewIcon from '@material-ui/icons/PowerSettingsNew'
import { Face as FaceIcon, Notifications } from '@material-ui/icons'
import ClickAwayListener from '@material-ui/core/ClickAwayListener'
import { Link } from 'react-router-dom'
import logout from '../../../auth/controllers/logout'
import getData from '../../../auth/controllers/getUserData'
import styled from 'styled-components'
import DashboardSidenav from '../dashboard-sidenav/dashboardSidenav'
import logo from '../../../assets/images/pp_logo_white_font.png'
const WelcomeName = styled.span`
  font-size: 18px;
  color: #ffffff;
`

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    zIndex: 1500
  },
  toolbar: {
    paddingRight: 24 // keep right padding when drawer closed
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    background: '#444851',
    boxShadow: 'none'
  },
  title: {
    flexGrow: 1,
    fontWeight: 'bold',
    textAlign: 'center',
    fontSize: '26px',
    letterSpacing: '0.1px',
    opacity: '0.85'
  }
}))

const StyledLink = styled(Link)`
  text-decoration: none;
  color: rgba(0, 0, 0, 0.6);
`
const Img = styled.img`
  height: 34px;
  margin: 0 22px 0 6px;
`
const NotifIcon = styled(Notifications)`
  margin: 0 12px 0 auto;
`

const HeadMenu = (props) => {
  const classes = useStyles()
  const [open, setOpen] = useState(false)
  const [wholeName, setWholeName] = useState('Loading...')
  const [name, setName] = useState('')
  React.useEffect(() => {
    result()
  }, [])

  const result = async () => {
    const res = await getData()
    if (res) {
      setWholeName(`${res.first_name} ${res.last_name}`)
      setName(res.first_name)
    }
  }

  const drawerOpen = () => {
    setOpen(true)
  }
  const handleDrawerClose = () => {
    setOpen(!open)
  }

  const [_open, _setOpen] = React.useState(false)
  const anchorRef = React.useRef(null)

  function handleToggle() {
    _setOpen(prevOpen => !prevOpen)
  }

  function handleClose(event) {
    if (anchorRef.current && anchorRef.current.contains(event.target)) {
      return
    }

    _setOpen(false)
  }

  function jsUcfirst(string) {
    return string.charAt(0).toUpperCase() + string.slice(1)
  }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={clsx(classes.appBar)}>
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            onClick={drawerOpen}
            className={clsx(classes.menuButton)}
          >
            <MenuIcon />
          </IconButton>
          {/* <Typography color="inherit" noWrap className={classes.title}>
            Perfect Pitch Manager
          </Typography> */}
          <Img src={logo} alt="logo" />
          {name ? (
            <WelcomeName>
              {jsUcfirst(name)}, welcome to your dashboard!
            </WelcomeName>
          ) : null}
          <NotifIcon />
          <AccountCircleIcon fontSize="large" />
          <Typography>{wholeName}</Typography>
          <IconButton
            ref={anchorRef}
            onClick={handleToggle}
            style={{
              color: 'white'
            }}
          >
            <ExpandMoreIcon />
          </IconButton>
          <Popper
            open={_open}
            anchorEl={anchorRef.current}
            placement={'bottom'}
            transition
            disablePortal
          >
            {({ TransitionProps, placement }) => (
              <Grow
                in={_open}
                {...TransitionProps}
                style={{
                  transformOrigin:
                    placement === 'bottom' ? 'center top' : 'center bottom'
                }}
              >
                <Paper id="menu-list-grow">
                  <ClickAwayListener onClickAway={handleClose}>
                    <MenuList>
                      <StyledLink to="/change-password">
                        <MenuItem onClick={handleClose}>
                          <ListItemIcon>
                            <FaceIcon />
                          </ListItemIcon>
                          <ListItemText>
                            <Typography
                              style={{
                                fontSize: '15px'
                              }}
                            >
                              Change Password
                            </Typography>
                          </ListItemText>
                        </MenuItem>
                      </StyledLink>
                      <MenuItem
                        onClick={() => {
                          logout()
                        }}
                      >
                        <ListItemIcon>
                          <PowerSettingsNewIcon />
                        </ListItemIcon>
                        <ListItemText>
                          <Typography
                            style={{
                              fontSize: '15px'
                            }}
                          >
                            Logout
                          </Typography>
                        </ListItemText>
                      </MenuItem>
                    </MenuList>
                  </ClickAwayListener>
                </Paper>
              </Grow>
            )}
          </Popper>
        </Toolbar>
      </AppBar>

      {(props.location.pathname.includes('/manage') ||
        props.location.pathname.includes('/change')) && (
          <SideNav
            handleDrawerClose={handleDrawerClose}
            drawerOpen={drawerOpen}
            open={open}
          />
        )}

      {props.location.pathname.includes('/dashboard') && (
        <DashboardSidenav
          handleDrawerClose={handleDrawerClose}
          drawerOpen={drawerOpen}
          open={open}
          {...props}
        />
      )}
    </div>
  )
}

export { HeadMenu }
