import React, { useEffect, useState } from "react";
import { Card, Divider } from "@material-ui/core";
import {
  Pagination,
  HeaderLink,
  HeaderButton,
  SearchBar,
  FilterToolBar
} from "common-components";
import { NewUser } from "../users-new";
import { UserTable } from "../components";
import { useStyles } from "./styles";
const UserLanding = () => {
  const [users, setUsers] = useState([]);
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [is_new_user, setIsNewUser] = useState(false);

  const classes = useStyles();

  useEffect(() => {
    fetch("http://5e0015181fb99500141403a4.mockapi.io/mock/v1/users")
      .then((users) => users.json())
      .then((data) => {
        setUsers(data);
        setLoading(false);
      });
  }, []);

  const paginate: Function = (from: any, to: any) => {
    setUserData(users.slice(from, to));
  };

  const renderPagination: Function = () => {
    return (
      <>
        <Divider />
        <Pagination
          paginateFn={paginate}
          totalItems={users.length}
          itemsPerPage={10}
        />
      </>
    );
  };

  const renderHeader: Function = () => {
    return (
      <div className={classes.header}>
        <HeaderLink
          pathSensitive={false}
          menu={[
            {
              title: "Campaigns",
              path: "/manage/campaigns"
            },
            {
              title: "Companies",
              path: "/manage/companies"
            },
            {
              title: "Locations",
              path: "/manage/locations"
            },
            {
              title: "Realms",
              path: "/manage/realms"
            },
            {
              title: "DID Pools",
              path: "/manage/did-pool"
            },
            {
              title: "Dids",
              path: "/manage/dids"
            }
          ]}
          title="Users"
        />
        <HeaderButton
          noIcon={false}
          buttonText="New User"
          openFunction={() => setIsNewUser(true)}
        />
      </div>
    );
  };

  const renderFilter: Function = () => {
    return (
      <>
        <SearchBar
          title="User"
          userData={users}
          headers={["username", "first_name", "last_name"]}
        />
        <Divider />
        <FilterToolBar
          sortBy={true}
          activeStatus={true}
          realm={false}
          company={true}
          campaign={true}
          hasCompany={true}
          roles={true}
          FilterApplyButton={(data: any) => console.log("Data: ", data)}
        />
      </>
    );
  };

  return (
    <>
      <NewUser open={is_new_user} onClose={() => setIsNewUser(false)} />
      {renderHeader()}
      <Card square={true}>
        {renderFilter()}
        <div style={{ minHeight: 500 }}>
          <UserTable users={userData} loading={loading} />
        </div>
        {!loading && renderPagination()}
      </Card>
    </>
  );
};
export { UserLanding };
