import React, { useState, useEffect } from 'react'
import { Button, Popover, MenuList, MenuItem } from '@material-ui/core'
import { Link } from 'react-router-dom'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore'

interface HeaderLinkProps {
  menu: Array<{ title: string; path: string }>
  pathSensitive?: boolean
  style?: Obj
  title: string
}
interface Obj {
  [index: string]: any
}

const HeaderLink: React.FC<HeaderLinkProps> = ({
  menu,
  pathSensitive,
  style,
  title
}) => {
  const [popper, setPopper] = useState<boolean>(false)
  const [anchorRef, setAnchorRef] = useState<any>(null)
  const checkLinks = () => {
    if (typeof window != 'undefined')
      menu.map(item => (window.location.pathname === item.path ? null : null))
  }

  useEffect(() => {
    if (pathSensitive) {
      checkLinks()
    }
  })

  const handlePopper = (event: any) => {
    setPopper(true)
    setAnchorRef(event)
  }
  const handlePopperClose = () => {
    setPopper(false)
  }

  return (
    <div
      style={{
        ...style
      }}
    >
      <Button
        style={{
          fontSize: '24px',
          textTransform: 'none',
          textDecoration: 'underline',
          color: '#444851'
        }}
        onClick={event => {
          handlePopper(event.currentTarget)
        }}
      >
        {title}

        <ExpandMoreIcon style={{ marginLeft: '3px' }}></ExpandMoreIcon>
      </Button>

      <Popover
        open={popper}
        anchorEl={anchorRef}
        onClose={() => {
          handlePopperClose()
        }}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'left'
        }}
        PaperProps={{
          square: true,
          style: {
            borderRadius: 1,
            marginTop: -3
          }
        }}
      >
        <MenuList style={{ width: '240px', color: ' #777777' }}>
          {menu.map((item, i) => (
            <div key={i}>
              <Link
                to={item.path}
                style={{
                  textDecoration: 'none',
                  color: ' #777777'
                }}
              >
                <MenuItem
                  style={{
                    fontSize: 15,
                    padding: 15
                  }}
                >
                  {item.title}{' '}
                </MenuItem>
              </Link>
            </div>
          ))}
        </MenuList>
      </Popover>
    </div>
  )
}

HeaderLink.defaultProps = {
  title: 'Test Header',
  menu: [
    {
      title: 'Test1',
      path: '/manage/1'
    },
    {
      title: 'Test2',
      path: '/manage/2'
    },
    {
      title: 'Test3',
      path: '/manage/3'
    },
    {
      title: 'Test4',
      path: '/manage/4'
    },
    {
      title: 'Test5',
      path: '/manage/5'
    },
    {
      title: 'Test6',
      path: '/manage/6'
    }
  ]
} as Partial<HeaderLinkProps>

export { HeaderLink }
